import React from 'react';
import './App.css';

// function Helloworld_fun(){
//   return (

//     <div className="hola" >Este es un div de prueba</div>
//   );
// }

function NavItem(props){
  return (
    <button className="navItem" > {props.texto} </button>
  );
}

function Header(){
  return (
    <div className="header" >

      <h2 id="weedway" ><img src="favicon.ico" alt="Canabis" />WeedWay</h2>
      <div className="navBar" >

        <NavItem texto="Home" />
        <NavItem texto="Mensajes" />
        <NavItem texto="Cuenta" />

      </div>
    </div>
  );
}

function PostImg(){
  return (
    <div className="img-post" >
      <div>
        <img src="logo192.png" />
        <h3 className="user-title" >User</h3>
      </div>
      <img src="logo512.png" alt="img-posted" />
      <p className="description" > <span>Usuario1</span> Este es mi primer post</p>
      <div className="comentar" >
        <input type="text" placeholder="Escribe aquí tu comentario..." />
        <button>Send</button>
      </div>
    </div>
  );
}

function Posteo(){
  return (
    <div>
      <PostImg/>
      <br/>
    </div>
  );
}

function Footer(){
  return(
    <div className="footer" >
      <NavItem texto="Foto" />
      <NavItem texto="Video" />
      <NavItem texto="Lugares" />
    </div>
  );
}

class Publicaciones extends React.Component {

  state = {
    show: true
  }

  render() {
    if(this.state.show){

      return (
        <div >
          <h2 className="user-title" >{this.props.texto}</h2>
          <button className="btn-block" onClick={ ()=> this.setState( {show: false} ) } >Cambiar estado</button>
        </div>

      );
    } else {
      return (
        <div >
          <h2 > Nada para mostrar </h2>
          <button className="btn-block" onClick={ ()=> { if(this.state.show){ this.setState( {show: false} )}else{ this.setState( {show: true} ) } } } >Cambiar estado</button>
        </div>
      );
    }
  }

}

function App() {
  return (
    <div className="App">
      <Header/>
      <Publicaciones texto="Andy" />
      <Posteo/>
      <Posteo/>
      <Posteo/>
      <Footer/>
    </div>
  );
}

export default App;
