import React from 'react';


export default class Foto extends React.Component{

    inputFile = {
        border: '1px dashed grey',
        display: 'inline-block',
        width: 'calc(100% - 80px)',
        padding: '40px',
        cursor: 'pointer',
        textAlign: 'center'
    }

    selected(){
        console.log( document.getElementById('inputFile') );
    }

    render(){
        return (
            <form>
                <p>Hola elige una FOTO para subir</p>
                <input id="inputFile" type="file" style={{ display: 'none' }} />
                <label for="inputFile" style={this.inputFile} onClick={this.selected()} > Select an image </label>
                <input type="button" value="Listo" />
            </form>
        );
    }
}
