import React from 'react';


export default class Foto extends React.Component{

    inputFile = {
        border: '1px dashed red',
        display: 'inline-block',
        padding: '6px 12px',
        cursor: 'pointer'
    }

    render(){
        return (
            <form>
                <p>Hola elige una FOTO para subir</p>
                <input id="inputFile" type="file" style={{ display: 'block', width: '100%', padding: '30px 0' }} />
                <label for="inputFile" style={this.inputFile} />
                <input type="button" value="Listo" />
            </form>
        );
    }
}
