import React from 'react';
import './App.css';

// function Helloworld_fun(){
//   return (

//     <div className="hola" >Este es un div de prueba</div>
//   );
// }

function HeaderTitle(){
  return (
    <div id="weedway">
      <h2> <img src="favicon.ico" alt="Canabis" /> WeedWay</h2>

    </div>
  );
}

function NavItem(props){
  return (
    <button className="navItem" > <i className={props.icon} > </i> {props.texto} </button>
  );
}

function Header(){
  return (

      <div className="header" >

        <div className="navBar" >

          <NavItem texto="Home" icon="fas fa-home" />
          <NavItem texto="Mensajes" icon="fas fa-envelope" />
          <NavItem texto="Cuenta" icon="fas fa-user-circle" />

        </div>
      </div>

  );
}

class InteractionFav extends React.Component{

  state = {
    pressed: false
  }

  render(){
    if(this.state.pressed){
      return(
        <i className="fas fa-heart interaction-button" onClick={ ()=>{ this.setState({pressed: false}) } } ></i>
      );
    } else {
      return (
        <i className="far fa-heart interaction-button" onClick={ ()=>{ this.setState( {pressed: true} ) } } ></i>
      );
    }
  }
}

class Comentario extends React.Component{

  render(){
    if ( this.props.show ){
      return (
        <div className="comentar " >
          <input type="text" placeholder="Escribe aquí tu comentario..." />
          <button>Send</button>
        </div>
      );
    } else {
      return (
        <div className="comentar d-none" >
          <input type="text" placeholder="Escribe aquí tu comentario..." />
          <button>Send</button>
        </div>
      );
    }
  }
}

class Interaction extends React.Component {

  state = {
    show: false
  }

  render(){
    return (
      <div className="interaction-section" >
        <InteractionFav/>
        <i className="far fa-comment interaction-button" onClick={ ()=>{this.setState( {show: !this.state.show} ); } } ></i>
        <i className="far fa-share interaction-button" ></i>
        <p className="description" > <span>Usuario1</span> Este es mi primer post</p>
        <Comentario show={this.state.show} />
      </div>
    );
  }
}

class Dropdown extends React.Component{

  state={
    show: false
  }

  render(){
    if(this.state.show){
      return (
        <div>
          <div className="dropdown" >
            <p>Dejar de seguir a User</p>
            <p>Compartir publicación de User</p>
            <p>Reportar a User</p>
          </div>
          <i className="fas fa-ellipsis-h options" onClick={ ()=>{this.setState({show: !this.state.show})} } ></i>
        </div>
      );
    }else{
      return (
        <div>
          <i className="fas fa-ellipsis-h options" onClick={ ()=>{this.setState({show: !this.state.show})} } ></i>
        </div>
      );
    }
  }
}

function PostImg(){
  return (
    <div className="post-full" >
      <div className="userName" >
        <img className="fondo-negro user-photo" src="logo192.png" alt="user-logo" />
        <h3 className="user-title" >User</h3>
        <Dropdown/>
      </div>
      <img className=" img-post fondo-negro" src="logo512.png" alt="img-posted" />
      <Interaction/>
    </div>
  );
}

function Posteo(){
  return (
    <div>
      <br/>
      <PostImg/>
      <br/>
    </div>
  );
}

function Footer(){
  return(
    <div className="footer" >
      <NavItem texto="Foto" icon="fas fa-camera" />
      <NavItem texto="Video" icon="fas fa-video" />
      <NavItem texto="Lugares" icon="fas fa-map-marked-alt" />
    </div>
  );
}

function Modal(){
  return (
    <div>
      <h2>Modal Example</h2>

      {/* <!-- Trigger/Open The Modal --> */}
      <button id="myBtn">Open Modal</button>

      {/* <!-- The Modal --> */}
      <div id="myModal" className="modal">

        {/* <!-- Modal content --> */}
        <div className="modal-content">
          <span className="close">&times;</span>
          <h2>Cuenta personal</h2>
          <p>Some text in the Modal..</p>
          <p>Some text in the Modal..</p>
          <p>Some text in the Modal..</p>
          <p>Some text in the Modal..</p>
          <h4>Cuenta personal</h4>
          <input type="text" />
          <p>Some text in the Modal..</p>
        </div>

      </div>

    </div>
  );
}

function App() {
  return (
    <div className="App">

      <HeaderTitle/>
      <Header/>
      <Posteo/>
      <Posteo/>
      <Posteo/>
      <Modal/>
      <Footer/>
    </div>
  );
}

export default App;
