import React from 'react';


class NavItem extends React.Component {

  state = {
    show: false
  }

  styling () {
    const {show} = this.state;
    if(show){
      return {
        background: 'green',
        color: 'blue',
        border: '2px yellow solid'
      };
    }
    else{
      return {
        background: 'red',
        color: 'grey',
        border: '2px white solid'
      };
    }
  }

  render(){
    const {props} = this;
    return (
      <button className="navItem" style={this.styling()} onClcick={ ()=>{this.setState({show: false})} } > <i className={props.icon} > </i> {props.texto} </button>
    );
  }

}

export default NavItem;
