import React from 'react';


class NavItem extends React.Component {

  state = {
    show: false
  }

  styling () {
    // const {show} = this.state;
    if(this.state.show){
      return {
        background: 'green',
        color: 'blue',
        border: '2px yellow solid',
        padding: '0px',
        lineHeight: '6px',
        fontSize: '16px'
      };
    }
    else{
      return {
        background: 'red',
        color: 'grey',
        border: '2px white solid'
      };
    }
  }

  render(){
    const {props} = this;
    return (
      <button className="navItem" style={this.styling()} onClick={ ()=>{this.setState({show: !this.state.show})} } > <i className={props.icon + ' navIcon'} > </i> {props.texto} </button>
    );
  }

}

export default NavItem;
