import React from 'react';

// Static Files
import imgTitle from './weedway-icon.png';

class LoginForm extends React.Component{

  data = {
    name: '',
    password: ''
  }
  usernameStyle = ()=>{
    if( this.data.name === "" ){
      return ("username-label");
    }
    else{
      return("username-label-focused");
    }
  }
  passwordStyle = ()=>{
    if( this.data.password === "" ){
      return ("password-label");
    }
    else{
      return("password-label-focused");
    }
  }

  onchangeUser = e => {
    // console.log(e.target);
    this.data = { name: e.target.value, password: this.data.password };
  }
  onchangePass = e => {
    // console.log(e.target);
    this.data = { name: this.data.name, password: e.target.value };
  }

  onsubmit = e => {
    // e.preventDefault();
    if( this.data.name !== '' ){
      localStorage.setItem('weedway-user', this.data.name);
    }else{
      e.preventDefault();
      alert("Usuario vacío")
      // document.
    }
    //e.target[0].value = ''; // e.target[0] es el input correspondiente al post
  }

  render(){
    return (
      <div className="animate__animated animate__fadeInUp" >

        <form onSubmit={this.onsubmit} autoComplete="on" id="formToAtuh" >
          <input type='text' className="inputForm" onChange={this.onchangeUser} autoComplete="on" id='username-login' />
          <label htmlFor="username-login" className={ this.usernameStyle() } >Usuario</label>

          <input type='password' className="inputForm" onChange={this.onchangePass} id='password-login' />
          <label htmlFor="password-login" className={ this.passwordStyle() } /*"password-label"*/ >Contraseña</label>

          <div style={{display: 'flex', margin: '20px'}} >
            <button id="swapSession" onClick={ ()=>{ this.cambiarEstado() } } >Crear cuenta</button>
            <button type='submit' id="login-btn">Log in</button>
          </div>
        </form>
      </div>
    );
  }
}

class RegisterForm extends React.Component{

  onsubmit = e => {
    // e.preventDefault();
    if( this.data.name !== '' ){
      localStorage.setItem('weedway-user', this.data.name);
    }else{
      e.preventDefault();
      alert("Usuario vacío")
      // document.
    }
    //e.target[0].value = ''; // e.target[0] es el input correspondiente al post
  }

  render(){
    return (
      <div className="animate__animated animate__fadeInUp" >

        <form onSubmit={this.onsubmit} autoComplete="on" id="formToAtuh" >

          <input type="text" className="inputForm" placeholder="Usuario" />
          <label htmlFor="username-login" >Usuario</label>

          <input type="text" className="inputForm" placeholder="Correo" />
          <label htmlFor="username-login" >Usuario</label>

          <input type="text" className="inputForm" placeholder="Edad" />
          <label htmlFor="username-login" >Usuario</label>

          <input type="text" className="inputForm" placeholder="Nueva contraseña" />
          <label htmlFor="username-login" >Usuario</label>

          <input type="text" className="inputForm" placeholder="Confirmar contraseña" />
          <label htmlFor="username-login" >Usuario</label>

          <div style={{display: 'flex', margin: '20px'}} >
            <button id="swapSession" onClick={ ()=>{ this.cambiarEstado() } } >Prefiero iniciar sesión</button>
            <button type='submit' onClick={ ()=>{alert("Registrado con exito")} } id="login-btn" >Register</button>
          </div>
        </form>

      </div>
    );
  }
}

