import React from 'react';

class Modal extends React.Component{

    state = {
        show: this.props.estado
    }

    modalStyle = {
        background: 'red',
        color: 'white',
        position: 'fixed',
        top: '30vh',
        left: '300px',
        right: '300px',
        zIndex: '1'
    }

    animation = () => {
        const entrance = " animate__backInUp";
        const exit = " animate__backOutDown";

        if(this.state.show){
            return (entrance);
        }else{
            return (exit);
        }
    }

    render(){

        return (
            <div className={"animate__animated" + this.animation() }  style={this.modalStyle}>
                <p >Modal showing</p>
                <button onClick={ ()=>{this.setState({show: !this.state.show})} } >Press to show Modal</button>
            </div>
        );

    }
}

export default Modal;
