import React from 'react';

class Modal extends React.Component{

    state = {
        show: false
    }

    modalStyle = {
        background: 'red',
        color: 'white',
        position: 'fixed',
        top: '300px',
        left: '300px',
        right: '300px',
        zIndex: '1'
    }

    animation = () => {
        const entrance = "animate__backInUp";
        const exit = "animate__backInDown";

        if(this.state.show){
            return (exit);
        }else{
            return (entrance);
        }
    }

    render(){

        if(this.state.show){
            return (
                <div className={"animate__animated " + this.animation } style={this.modalStyle}>
                    <p >Modal showing</p>
                    <button onClick={ ()=>{this.setState({show: !this.state.show})} } >Press to show Modal</button>
                </div>
            );
        }else{
            return (
                <div>
                    <button onClick={ ()=>{this.setState({show: !this.state.show})} } >Press to show Modal</button>
                </div>
            );
        }


    }
}

export default Modal;
