import React from 'react';


class Cuenta extends React.Component{

    input = {
        display: 'block',
        width: '80%',
        margin: 'auto'
    }

    edit = {
        display: 'block',
        width: '80%',
        margin: 'auto',
        backgroundColor: 'lightgreen'
    }

    logOut = {
        display: 'block',
        width: '70%',
        margin: '5px auto',
        color: 'white',
        backgroundColor: 'red',
        borderRadius: '2rem'
    }

    render(){
        return (
            <div style={{maxWidth: '300px', margin: 'auto'}} >
                <h5 style={{textAlign: 'center', margin: '0', fontSize: '40px', fontWeight: 'bold', overflow: 'hidden'}} >{localStorage.getItem('weedway-user')}</h5>
                <p>Aqui puedes ver tus datos personales y posteos</p>
                <button style={this.edit} >Editar datos personales</button>
                <button style={this.logOut} onClick={ ()=>{localStorage.removeItem('weedway-user'); window.location.reload()} } >Cerrar sesi√≥n</button>
            </div>
        );
    }
}

export default Cuenta;
