import React from 'react';


class EditarDatos extends React.Component{

    state = {
        show: true
    }

    edit = {
        display: 'block',
        width: '80%',
        margin: 'auto',
        backgroundColor: 'lightgreen'
    }

    render(){
        if( this.state.show ){
            return (
                <button style={this.edit} onClick={ ()=>{this.setState({show: false})} } >Editar datos personales</button>
            );
        }else {
            return (
                <form>
                    <input className="animate__animated animate__fadeInUp" type="text" placeholder="Username" />
                    <input type="submit" />
                </form>
            );
        }
    }
}

class Cuenta extends React.Component{

    input = {
        display: 'block',
        width: '80%',
        margin: 'auto'
    }

    logOut = {
        display: 'block',
        width: '70%',
        margin: '5px auto',
        padding: '6px',
        color: 'white',
        backgroundColor: 'red',
        borderRadius: '2rem'
    }

    render(){
        return (
            <div style={{maxWidth: '300px', margin: 'auto'}} >
                <h5 style={{textAlign: 'center', margin: '0', fontSize: '40px', fontWeight: 'bold', overflow: 'hidden'}} >{localStorage.getItem('weedway-user')}</h5>
                <p>Aqui puedes ver tus datos personales y posteos</p>

                <EditarDatos />

                <button style={this.logOut} id='logOut' onClick={ ()=>{localStorage.removeItem('weedway-user'); window.location.reload()} } >Cerrar sesi√≥n</button>
            </div>
        );
    }
}

export default Cuenta;
