class App extends React.Component {

  state = {
    modal: false,
    textoModal: ''
  }

  changEstado = (texto) => {
    this.setState({modal: !this.state.modal, textoModal: texto});
    console.log(this.state);
  }

  render(){
    return (
      <div className="App">

        <HeaderTitle/>
        <Header setEstado={this.changEstado} />
        <Modal estado={this.state.modal} setEstado={this.changEstado} />
        <Posteo/>
        <Posteo/>
        <Posteo/>
        <Footer setEstado={this.changEstado} />
      </div>
    );
  }

}

export default App;
