import React from 'react';

// background-color: rgba(255,228,196,0.6);

export default class Options extends React.Component{

    fondo = {
      position: 'fixed',
      top: '0',
      left: '0',
      right: '0',
      bottom: '0',
      display: 'flex',
      alignItems: 'center',
      padding: '20px',
      backgroundColor: 'rgba(30,30,30,0.8)',
      zIndex: '4'
    }
    containerStyle = {
        background: 'rgba(255,228,196,0.2)',
        maxWidth: '500px',
        margin: 'auto',
        padding: '10px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '20px',
        height: '230px',
    }
    // buttons = {
    //   borderRadius: '10px'
    // }
    closeButton = {
        fontSize: '26px',
        color: 'white',
        position: 'fixed',
        top: '-30px',
        right: '6px',
        cursor: 'pointer'
    }

    render(){
        return (
            <div style={this.fondo} >
                <div className="animate__animated animate__fadeInUp" style={this.containerStyle} >
                    <i className="fas fa-times " style={this.closeButton} onClick={ ()=>{this.props.options(); } } ></i>
                    <button  className="btn-block btn-options" >Dejar de seguir a <span style={{fontWeight: 'bold'}} >"{this.props.user}"</span></button>
                    <button  className="btn-block btn-options" >Compartir publicaci√≥n de <span style={{fontWeight: 'bold'}} >"{this.props.user}"</span></button>
                    <button  className="btn-block btn-options" >Reportar a <span style={{fontWeight: 'bold'}} >"{this.props.user}"</span></button>
                </div>
            </div>
        );

    }
}
