import React from 'react';
import NavItem from './navItem.js';


function HeaderTitle(props){
    return (
      <div style={{border: '2px lightgreen solid', display: 'flex', width: 'calc(100% - 4px)'}/* El -8px es por los bordes */} >

        <div style={{width: '80%'}} id="weedway">
          <h2 > <a href="/"> <img src="favicon.ico" alt="weedway-icon" /> WeedWay </a> </h2>
        </div>
        <button style={{width: '10%', minWidth: '100px', color: 'rgb(40,230,40)', backgroundColor: 'rgb(50,50,50)' }} className="nav-button" onClick={ ()=>{ props.setEstado('Buscar') } } > <i className={'fas fa-search' + ' navIcon'} > </i> <span style={{fontSize: '20px'}} > Buscar </span> </button>
        <button style={{width: '10%', minWidth: '100px', color: 'rgb(40,230,40)', backgroundColor: 'rgb(50,50,50)' }} className="nav-button" onClick={ ()=>{ props.setEstado('cuenta') } } > <i className={'fas fa-user' + ' navIcon'} > </i> <span style={{fontSize: '20px'}} > Cuenta </span> </button>
      </div>
    );
}

export default HeaderTitle;
