import React from 'react';
import './App.css';

// function Helloworld_fun(){
//   return (

//     <div className="hola" >Este es un div de prueba</div>
//   );
// }

function HeaderTitle(){
  return (
    <div id="weedway">
      <h2>WeedWay</h2>
      <img src="favicon.ico" alt="Canabis" />
    </div>
  );
}

function NavItem(props){
  return (
    <button className="navItem" > {props.texto} </button>
  );
}

function Header(){
  return (

      <div className="header" >

        <div className="navBar" >

          <NavItem texto="Home" />
          <NavItem texto="Mensajes" />
          <NavItem texto="Cuenta" />

        </div>
      </div>

  );
}

class Comentario extends React.Component{

  state = {
    show: false
  }

  render(){
    if ( this.state.show ){
      return (
        <div className="comentar" >
          <input type="text" placeholder="Escribe aquÃ­ tu comentario..." />
          <button>Send</button>
        </div>
      );
    } else {
      return (
        <div>
          <button className="press-comentario" onClick={ ()=>{ this.setState({show: true}) } } >Press to coment</button>
        </div>
      );
    }
  }

}

function Interaction(){
  return (
    <div className="interaction-section" >
      <img className="interaction-button" src="icons/heart-regular.svg" alt="heart" />
      <img className="interaction-button" src="icons/comment-regular.svg" alt="comment" />
      <img className="interaction-button" src="icons/share-square-regular.svg" alt="share" />
    </div>
  );
}

function PostImg(){
  return (
    <div className="post-full" >
      <div className="userName" >
        <img className="fondo-negro user-photo" src="logo192.png" alt="user-logo" />
        <h3 className="user-title" >User</h3>
      </div>
      <img className=" img-post fondo-negro" src="logo512.png" alt="img-posted" />
      <Interaction/>
      <p className="description" > <span>Usuario1</span> Este es mi primer post</p>
      <Comentario/>
    </div>
  );
}

function Posteo(){
  return (
    <div>
      <br/>
      <PostImg/>
      <br/>
    </div>
  );
}

function Footer(){
  return(
    <div className="footer" >
      <NavItem texto="Foto" />
      <NavItem texto="Video" />
      <NavItem texto="Lugares" />
    </div>
  );
}

function App() {
  return (
    <div className="App">

      <HeaderTitle/>
      <Header/>
      <Posteo/>
      <Posteo/>
      <Posteo/>
      <Footer/>
    </div>
  );
}

export default App;
