  render(){
    const {props} = this;
    return (
      <button className="navItem" onClick={ ()=>{this.setState({show: !this.state.show})} } > <i className={props.icon + ' navIcon'} > </i> <span> {props.texto} </span> </button>
    );
  }

}

export default NavItem;
