import React from 'react';

class InteractionFav extends React.Component{

    state = {
      pressed: false
    }

    render(){
      if(this.state.pressed){
        return(
          <i className="fas fa-heart interaction-button" onClick={ ()=>{ this.setState({pressed: false}) } } ></i>
        );
      } else {
        return (
          <i className="far fa-heart interaction-button" onClick={ ()=>{ this.setState( {pressed: true} ) } } ></i>
        );
      }
    }
  }

class Comentario extends React.Component{

    render(){
      if ( this.props.show ){
        return (
          <div className="comentar " >
            <input type="text" placeholder="Escribe aquí tu comentario..." />
            <button>Send</button>
          </div>
        );
      } else {
        return (
          <div className="comentar d-none" >
            <input type="text" placeholder="Escribe aquí tu comentario..." />
            <button>Send</button>
          </div>
        );
      }
    }
  }

class Interaction extends React.Component {

    state = {
      show: false
    }

    render(){
      return (
        <div className="interaction-section" >
          <InteractionFav/>
          <i className="far fa-comment interaction-button" onClick={ ()=>{this.setState( {show: !this.state.show} ); } } ></i>
          <i className="far fa-share interaction-button" ></i>
          <p className="description" > <span>Usuario1</span> Este es mi primer post</p>
          <p className="description comentario" > <span>Usuario2</span> Que wapo estas &#8804 3 </p>
          <Comentario show={this.state.show} />
        </div>
      );
    }
  }

class Dropdown extends React.Component{

    state={
      show: false
    }

    render(){
      if(this.state.show){
        return (
          <div>
            <div className="dropdown smoothDown" >
              <button>Dejar de seguir a User</button>
              <button>Compartir publicación de User</button>
              <button>Reportar a User</button>
            </div>
            <i className="fas fa-ellipsis-h options" onClick={ ()=>{this.setState({show: !this.state.show}); console.log(this);} } ></i>
          </div>
        );
      }else{
        return (
          <div>
            <i className="fas fa-ellipsis-h options" onClick={ ()=>{this.setState({show: !this.state.show})} } ></i>
          </div>
        );
      }
    }
  }

function PostImg(){
    return (
      <div className="post-full" >
        <div className="userName" >
          <img className="fondo-negro user-photo" src="logo192.png" alt="user-logo" />
          <h3 className="user-title" >User</h3>
          <Dropdown/>
        </div>
        <img className=" img-post fondo-negro" src="logo512.png" alt="img-posted" />
        <Interaction/>
      </div>
    );
  }

  function Posteo(){
    return (
      <div>
        <br/>
        <PostImg/>
        <br/>
      </div>
    );
  }

export default Posteo;
