import React from 'react';

class Modal extends React.Component{

    modalStyle = {
        background: 'red',
        color: 'white',
        position: 'fixed',
        top: '0vh',
        bottom: '0vh',
        left: '0px',
        right: '0px',
        width: 'auto',
        zIndex: '1'
    }

    animation = () => {
        const entrance = " animate__backInUp";
        const exit = " animate__backOutDown";

        if(this.props.estado){
            return (entrance);
        }else{
            return (exit);
        }
    }

    render(){

        if(this.props.view == 'Mensajes'){
            // return <Mensajes/>;
            return <div>Mensajes JUA JUA</div>

        }else if(this.props.view == 'Cuenta'){
            // return <Mensajes/>;
            return <div>Cuenta JUA JUA</div>

        }else if(this.props.view == 'Foto'){
            // return <Mensajes/>;
            return <div>Foto JUA JUA</div>
        }

        return (
            <div className={"animate__animated" + this.animation() }  style={this.modalStyle}>
                <p style={{textAlign: 'center'}} >Modal showing in {this.props.view} </p>
                <button onClick={ ()=>{this.props.setEstado()} } >Close Modal</button>
            </div>
        );

    }
}

export default Modal;
