import React from 'react';


class Options extends React.Component{

    modalStyle = {
        background: 'red',
        position: 'absolute',
        top: '70px',
        bottom: '-300px',
        left: '20px',
        right: '20px',
        width: 'auto',
        zIndex: '2'
    }

    buttonStyle = {
        color: 'white',
        display: 'block',
        width: '100%'
    }

    render(){

        return (
            <div
            className="animate__animated animate__fadeInUp"
            style={this.modalStyle} >
                <div style={{ position: 'absolute', top: '20%', left: '20%', right: '20%', bottom: '20%', zIndex: '2'}} >
                    <button style={this.buttonStyle} >Dejar de seguir a User</button>
                    <button style={this.buttonStyle} >Compartir publicaci√≥n de User</button>
                    <button style={this.buttonStyle} >Reportar a User</button>
                </div>
                <i className="fas fa-ellipsis-h options" onClick={ ()=>{this.props.setEstado(); } } ></i>
            </div>
        );

    }
}

export default Options;
