import React from 'react';

export default class MenuLateral extends React.Component{ // Se va a un Componente

    estiloMenuLateral = {
      position: 'fixed',
      right: '0',
      top: '0',
      bottom: '0',
      width: '40%',
      minWidth: '300px',
      maxWidth: '500px',
      backgroundColor: 'rgb(30,30,30)',
      boxShadow: '0px 0px 16px 10px rgb(20,20,20)',
      zIndex: '1'
    }

    animation = ()=>{
      const entrance = " animate__backInRight";
      const exit = " animate__backOutRight";

      console.log(this.props);

      // return exit;

      if(this.props.estado){
        return entrance;
      }else {
        return exit;
      }
    }

    render(){
      const entrance = "animate__backInRight";
      const exit = "animate__backOutRight";

      return (
        <div style={this.estiloMenuLateral} className={ "animate__animated " } id='menuLateral' >
          Hola soy el menu lateral que aparezco de la derecha
          <button
            onClick={ ()=>{
              this.props.showMenu();
              console.log('Si anda el boton de cerrar menu');
              if(this.props.estado){
                document.getElementById('menuLateral').classList.remove(entrance);
                document.getElementById('menuLateral').classList.add(exit);
              }else{
                document.getElementById('menuLateral').classList.remove(exit);
                document.getElementById('menuLateral').classList.add(entrance);
              }
              }}
            >&times; Cerrar Menu</button>
        </div>
      );
    }

  }
