import React from 'react';


class Estilos extends React.Component {

  state = {
    show: true
  }

  render () {
    const {show} = this.state;
    if(show){
      return {
        background: 'green',
        color: 'blue',
        border: '2px yellow solid'
      };
    }
    else{
      return {
        background: 'red',
        color: 'grey',
        border: '2px white solid'
      };
    }
  }

}

function NavItem(props){
    return (
      <button className="navItem" style={Estilos.render()} > <i className={props.icon} > </i> {props.texto} </button>
    );
  }

export default NavItem;
