import React from 'react';

import HeaderTitle from './Components/headerTitle.js';
import Modal from './Components/modal.js';
import Posteo from './Components/postImg.js';
import Footer from './Components/footer.js';


class Login extends React.Component{

  container = {
    margin: '10vw',
    padding: '0 30px',
    background: 'tomato',
    border: '2px red solid',
    borderRadius: '20px'
  }

  // subContainer = {
  //   p
  // }

  inputs = {
    display: 'block'
  }

  render(){
    return (
      <div style={this.container} >
        <h3 style={{textAlign: 'center'}} >Login</h3>
        <label for='username' >Nombre de usuario</label>
        <input type='text' style={this.inputs} id='username' />
        <label for='password-user' >Nombre de usuario</label>
        <input type='password' style={this.inputs} id='password-user' />
      </div>
    );
  }
}

class App extends React.Component {

  state = {
    modal: false
  }
  textoModal = '';

  changEstado = (texto) => {
    this.setState({modal: !this.state.modal});
    this.textoModal = texto;
  }

  usuarios = [
    {
      userName: 'andynievas92',
      profilePhoto: 'https://thispersondoesnotexist.com/image',
      userPost: 'https://thispersondoesnotexist.com/image'
    },
    {
      userName: 'reyJulian',
      profilePhoto: 'https://thispersondoesnotexist.com/image',
      userPost: 'https://thispersondoesnotexist.com/image'
    },
    {
      userName: 'mamfristo',
      profilePhoto: 'https://thispersondoesnotexist.com/image',
      userPost: 'https://thispersondoesnotexist.com/image'
    },
    {
      userName: 'salchipapa',
      profilePhoto: 'https://thispersondoesnotexist.com/image',
      userPost: 'https://thispersondoesnotexist.com/image'
    },
    {
      userName: 'ndeaaaAa',
      profilePhoto: 'https://thispersondoesnotexist.com/image',
      userPost: 'https://thispersondoesnotexist.com/image'
    },
    {
      userName: 'otros',
      profilePhoto: 'https://thispersondoesnotexist.com/image',
      userPost: 'https://thispersondoesnotexist.com/image'
    }
  ]

  render(){

    if( localStorage.getItem('weedway-user') ){
      return (
        <div className="App">

          <HeaderTitle setEstado={this.changEstado} />
          <Modal estado={this.state.modal} setEstado={this.changEstado} view={this.textoModal} />

          {this.usuarios.map( user=><Posteo posteo={user} /> ) }

          {/* {this.posts.map( post => <Posteo key={post.id} posteo={post} /> )} */}

          {/* userName={post.userName} profilePhoto={post.profilePhoto} userPost={post.userPost} */}

          <Footer setEstado={this.changEstado} />
        </div>
      );
    } else {
      return <Login />;
    }


  }

}

export default App;
