import React from 'react';


class Options extends React.Component{

    modalStyle = {
        background: 'rgba(255,228,196,0.7)',
        backdropFilter: 'blur(8px)',
        position: 'absolute',
        top: '60px',
        bottom: '-300px',
        left: '0px',
        right: '0px',
        width: 'auto',

        zIndex: '2'
    }

    containerStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
    }

    buttonStyle = {
        display: 'block',
        width: '100%'
    }

    render(){

        return (
            <div
            className="animate__animated animate__fadeInUp"
            style={this.modalStyle} >
                <div style={this.containerStyle} >
                    <button className="btn-block btn-options" >Dejar de seguir a User</button>
                    <button className="btn-block btn-options" >Compartir publicaci√≥n de User</button>
                    <button className="btn-block btn-options" >Reportar a User</button>
                </div>
                <i className="fas fa-ellipsis-h options" onClick={ ()=>{this.props.setEstado(); } } ></i>
            </div>
        );

    }
}

export default Options;
