import React from 'react';
import ModalBody from './views';

class Modal extends React.Component{

    modalStyle = {
        background: 'red',
        color: 'white',
        position: 'fixed',
        top: '0vh',
        bottom: '0vh',
        left: '0px',
        right: '0px',
        width: 'auto',
        zIndex: '1'
    }

    animation = () => {
        const entrance = " animate__backInUp";
        const exit = " animate__backOutDown";

        if(this.props.estado){
            return (entrance);
        }else{
            return (exit);
        }
    }

    render(){

        if(this.props.view === 'Home'){
            return <span></span>;
        }
        else {
            return (
                <div className={"animate__animated" + this.animation() }  style={this.modalStyle}>
                    <p style={{textAlign: 'center'}} >Modal showing in {this.props.view} </p>
                    <button onClick={ ()=>{this.props.setEstado()} } >Close Modal</button>
                    <ModalBody view={this.props.view} />
                </div>
            );
        }



    }
}

export default Modal;
