import React from 'react';


class Mensaje extends React.Component{

    styles = {
        fontSize: '20px',
        fontWeight: 'bold'
    }

    render(){
        return (
            <p> <span style={this.styles} > {this.props.userName} </span> {this.props.message} </p>
        );
    }
}

